mapper.update:
update t_admin_right_category 
	set update_Date = now()
		,category_name = #{categoryName}
		,sort_id = #{sortId}
		,create_date = #{createDate}
		,update_date = #{updateDate}
where category_id = #{categoryId}

mapper.select:
		select 
				category_id
				,category_name
				,sort_id
				,create_date
				,update_date
		 from
		t_admin_right_category
		
 			
			category_name = #{categoryName}
			
			
			and sort_id = #{sortId}
			 
			
			and create_date = #{createDate}
			 
			
			and update_date = #{updateDate}
			 
		
		order by sort_id ,create_date desc limit 1;

 		select 
				category_id
				,category_name
				,sort_id
				,create_date
				,update_date
 		 from t_admin_right_category
 		
 			
			category_name = #{categoryName}
			
			
			and sort_id = #{sortId}
			 
			
			and create_date = #{createDate}
			 
			
			and update_date = #{updateDate}
			 
 		
 		order by sort_id ,create_date desc;

		select 
				category_id
				,category_name
				,sort_id
				,create_date
				,update_date
 		 from t_admin_right_category
 		
 			
			category_id = #{rightCategoryVo.categoryId} 
				
			
			and category_name = #{rightCategoryVo.categoryName}
			 
			
			and sort_id = #{rightCategoryVo.sortId}
			 
			
				and create_date >= #{rightCategoryVo.startDate} and create_date <= #{rightCategoryVo.endDate}
			
 		
 		order by sort_id ,create_date desc   
 		
 		 limit ${page.begin} , ${page.limit};

		select count(1)  from t_admin_right_category
 		
 			
			category_id = #{rightCategoryVo.categoryId} 
				
			
			and category_name = #{rightCategoryVo.categoryName}
			 
			
			and sort_id = #{rightCategoryVo.sortId}
			 
			
				and create_date >= #{rightCategoryVo.startDate} and create_date <= #{rightCategoryVo.endDate}
			
 		
 		order by sort_id ,create_date desc ;
